SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_se_dt_lib_dias ( nr_prescricao_p bigint, nr_dias_param_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
dt_liberacao_w	timestamp;
nr_dias_w	bigint;


BEGIN

IF (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') THEN

	SELECT	MAX(dt_liberacao)
	INTO STRICT	dt_liberacao_w
	FROM	prescr_medica
	WHERE	nr_prescricao = nr_prescricao_p;

	IF (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') THEN

		SELECT	Obter_Dias_Entre_Datas(dt_liberacao_w, clock_timestamp())
		INTO STRICT	nr_dias_w
		;

		IF (nr_dias_w > nr_dias_param_p) THEN
			ds_retorno_w	:= 'N';
		ELSE
			ds_retorno_w	:= 'S';
		END IF;

	END IF;

END IF;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
