CREATE OR REPLACE FUNCTION lab_obter_se_exames_entregues ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_pessoa_fisica_w	varchar(10);
lista_nr_prescricao_w	varchar(255);
nr_prescricao_w		bigint;

c01 CURSOR FOR
SELECT	d.nr_prescricao
from (select a.nr_prescricao, c.dt_prescricao
	from	prescr_procedimento a,
		prescr_medica c
	where	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
	and	c.nr_prescricao = a.nr_prescricao
	and	not exists (	select b.nr_sequencia
				from	lab_entrega_exame b
				where  b.nr_prescricao = a.nr_prescricao
				and	b.nr_seq_prescr = a.nr_sequencia)
	and	a.nr_prescricao < nr_prescricao_p
	and	c.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	a.ie_status_atend >= 40
	and	coalesce(a.dt_baixa::text, '') = ''
	order by a.nr_prescricao desc) d LIMIT 10;


BEGIN

select cd_pessoa_fisica
into STRICT cd_pessoa_fisica_w
from prescr_medica
where nr_prescricao = nr_prescricao_p;

lista_nr_prescricao_w 	:= '';
nr_prescricao_w 	:= 0;

open c01;
loop
	fetch c01 into nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		lista_nr_prescricao_w := lista_nr_prescricao_w || nr_prescricao_w || ', ';
	end;
end loop;
close c01;

lista_nr_prescricao_w := substr(lista_nr_prescricao_w, 1, length(lista_nr_prescricao_w)-2);

return	lista_nr_prescricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
