CREATE OR REPLACE FUNCTION lab_obter_se_exame_aprov_ficha (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

				
				
ie_exames_aprovados_w	varchar(1) := 'N';				
qt_exames_prescricao_w	bigint;
qt_exames_aprovados_w	bigint;
nr_seq_lote_w		lote_ent_secretaria.nr_sequencia%type;


BEGIN

select	coalesce(max(nr_seq_lote_sec),0)
into STRICT	nr_seq_lote_w
from	lote_ent_sec_ficha
where	nr_prescricao = nr_prescricao_p;

select 	count(*)
into STRICT	qt_exames_aprovados_w
from	lote_ent_sec_ficha a,
	prescr_procedimento b
where   a.nr_prescricao = b.nr_prescricao
and	coalesce(b.ie_exame_bloqueado,'N') = 'N'
and	a.nr_seq_lote_sec = nr_seq_lote_w
and	(b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '')
and	b.ie_status_atend >= 35
and	not exists (	SELECT	1
			from	prescr_procedimento x,
				lote_ent_sec_ficha y
			where	x.nr_prescricao = y.nr_prescricao
			and	y.nr_seq_lote_sec = a.nr_seq_lote_sec
			and	(x.nr_seq_exame IS NOT NULL AND x.nr_seq_exame::text <> '')
      and coalesce(x.IE_SUSPENSO, 'N') = 'N'
			and (x.ie_status_atend < 35
			or	coalesce(x.ie_exame_bloqueado, 'N') = 'S'));

if (qt_exames_aprovados_w > 0) then
	ie_exames_aprovados_w := 'S';
end if;

return	ie_exames_aprovados_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
