CREATE OR REPLACE FUNCTION lab_obter_se_exame_lib ( nr_seq_exame_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_grupo_w		bigint;
ie_possui_regra_w	varchar(1);
ie_exame_liberado_w	varchar(1);


BEGIN
ie_exame_liberado_w	:= 'S';
if (nr_seq_exame_p > 0) then
	select 	max(nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from 	exame_laboratorio
	where 	nr_seq_exame = nr_seq_exame_p;

	select 	coalesce(max('S'), 'N')
	into STRICT	ie_possui_regra_w
	from	EXAME_LAB_ESTAB
	where 	coalesce(nr_seq_exame,nr_seq_exame_p) = nr_seq_exame_p
	and	coalesce(nr_seq_grupo,nr_seq_grupo_w) = nr_seq_grupo_w  LIMIT 1;

	if (ie_possui_regra_w = 'S') then

		ie_exame_liberado_w	:= 'N';

		select 	coalesce(max('S'), 'N')
		into STRICT	ie_exame_liberado_w
		from	EXAME_LAB_ESTAB
		where 	coalesce(nr_seq_exame,nr_seq_exame_p) = nr_seq_exame_p
		and	coalesce(nr_seq_grupo,nr_seq_grupo_w) = nr_seq_grupo_w
		and	cd_estabelecimento = cd_estabelecimento_p  LIMIT 1;

	end if;
end if;
return ie_exame_liberado_w;

end;
