CREATE OR REPLACE FUNCTION lab_obter_se_exige_alt_peso (nr_prescricao_p bigint, nr_seq_prescr_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);


BEGIN

select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	prescr_medica e,
		exame_lab_material d,
		prescr_procedimento a
	where	a.nr_prescricao = e.nr_prescricao
	  and	a.nr_seq_exame = d.nr_seq_exame
	  and	d.nr_seq_material = Obter_Material_Exame_Lab(null, a.cd_material_exame, 1)
	  and	a.nr_prescricao = nr_prescricao_p
	  and	obter_se_contido(a.nr_sequencia ,(nr_seq_prescr_p)) = 'S'
	  and	coalesce(d.ie_peso_altura_rep,'N') = 'S'
	  and	((coalesce(e.qt_peso::text, '') = '') or (coalesce(e.qt_altura_cm::text, '') = ''));

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
