CREATE OR REPLACE FUNCTION lab_obter_se_feriado (cd_estabelecimento_p bigint, dt_parametro_p timestamp, ie_tipo_feriado_p bigint) RETURNS bigint AS $body$
DECLARE


ie_feriado_w	integer;


BEGIN

if (coalesce(ie_tipo_feriado_p,0) <> 0) then

	select	count(*)
	into STRICT	ie_feriado_w
	from	feriado
	where	cd_estabelecimento = cd_estabelecimento_p
	and	dt_feriado = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_parametro_p)
	and	coalesce(ie_tipo_feriado,ie_tipo_feriado_p) = ie_tipo_feriado_p;
else
	select	count(*)
	into STRICT	ie_feriado_w
	from	feriado
	where	cd_estabelecimento = cd_estabelecimento_p
	and	dt_feriado = ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_parametro_p);

end if;

RETURN ie_feriado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
