CREATE OR REPLACE FUNCTION lab_obter_se_laudo_entregue (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1);


BEGIN

ds_retorno_w := 'N';

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	lab_entrega_exame
	where	nr_prescricao = nr_prescricao_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
