CREATE OR REPLACE FUNCTION lab_obter_se_permite_cancel (cd_motivo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	integer;


BEGIN

select 	coalesce(max(cd_tipo_baixa),0)
into STRICT	ds_retorno_w
from   	tipo_baixa_prescricao
where  	ie_prescricao_devolucao = 'P'
and 	ie_conta_paciente = 'N'
and 	cd_tipo_baixa = cd_motivo_p;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
