CREATE OR REPLACE FUNCTION lab_obter_se_prescr_mat_hl7_atx (nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_status_envio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_exam_mat_w		bigint;
qt_exam_mat_status_w	bigint;
cd_material_exame_w	varchar(20);
BEGIN

if (nr_prescricao_p > 0) and (ie_status_envio_p > 0) and (nr_seq_prescr_p > 0) then

	select	max(cd_material_exame)
	into STRICT	cd_material_exame_w
	from	prescr_procedimento
	where	nr_prescricao = nr_prescricao_p
	and	nr_sequencia = nr_seq_prescr_p;

	select	count(*)
	into STRICT	qt_exam_mat_w
	from	prescr_procedimento a
	where	a.nr_prescricao = nr_prescricao_p
	and	a.cd_material_exame = cd_material_exame_w
	and	a.nr_sequencia <> nr_seq_prescr_p
	and	a.ie_suspenso <> 'S';

	if (qt_exam_mat_w = 0) then --tratado para quando somente existir o item passado com o material em quest√£o
		ie_retorno_w := 'S';

	else

		select	count(*)
		into STRICT	qt_exam_mat_status_w
		from	prescr_procedimento a
		where	a.nr_prescricao = nr_prescricao_p
		and	a.cd_material_exame = cd_material_exame_w
		and	a.ie_status_atend = ie_status_envio_p
		and	a.nr_sequencia <> nr_seq_prescr_p
		and	a.ie_suspenso <> 'S';

		if (qt_exam_mat_status_w = qt_exam_mat_w) then
			ie_retorno_w := 'S';
		end if;

	end if;

end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
