SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_se_result_lib_visual ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, ie_tipo_result_p text) RETURNS varchar AS $body$
DECLARE

									
ie_retorna_aprov_w varchar(1);
ie_retorna_aprov_consult_w varchar(1);
nr_seq_exame_w bigint;


BEGIN

Select nr_seq_exame
Into STRICT nr_seq_exame_w
From prescr_procedimento
Where nr_prescricao = nr_prescricao_p
And nr_sequencia = nr_seq_prescr_p;

Select coalesce(IE_NECESSITA_APROV_LIB_RES,'N')
into STRICT ie_retorna_aprov_w
From exame_laboratorio
Where nr_seq_exame = nr_seq_exame_w;

if (ie_retorna_aprov_w <> 'S') then
    return 'S';
end if;

if (ie_retorna_aprov_w = 'S') then

    if (ie_tipo_result_p = 'R') then
      BEGIN
        Select coalesce(IE_RESULT_LIB_VISUALIZAR,'N')
        into STRICT ie_retorna_aprov_consult_w
        From RESULT_LABORATORIO_INFO
        Where nr_prescricao = nr_prescricao_p
        And nr_seq_prescr = nr_seq_prescr_p  LIMIT 1;

      EXCEPTION
        WHEN no_data_found THEN
        ie_retorna_aprov_consult_w := NULL;
      END;
        if (coalesce(ie_retorna_aprov_consult_w::text, '') = '') then
            return 'N';
        else
            return 'S';
        end if;
    end if;

    if (ie_tipo_result_p <> 'R') then
        return 'N';
    end if;
    
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
