CREATE OR REPLACE FUNCTION lab_obter_soro_amostra_rack (nr_seq_rack_p bigint) RETURNS varchar AS $body$
DECLARE


C01 CURSOR FOR
       SELECT distinct ds_amostra
         FROM lab_soro_amostra_rack
        WHERE ie_status <> 'L' and
              nr_seq_armazem_rack = nr_seq_rack_p;

ds_amostras_concat_w	varchar(255);
ds_amostra_w          varchar(100);

BEGIN
  ds_amostras_concat_w := null;

  open C01;
  loop
    fetch C01 into
      ds_amostra_w;
    EXIT WHEN NOT FOUND; /* apply on C01 */
      if (ds_amostras_concat_w IS NOT NULL AND ds_amostras_concat_w::text <> '') then
        ds_amostras_concat_w := ds_amostras_concat_w || ', ';
      end if;

      ds_amostras_concat_w := ds_amostras_concat_w || ds_amostra_w;
    end loop;
  close C01;

  return substr(ds_amostras_concat_w, 1, 255);
end;
