CREATE OR REPLACE FUNCTION lab_obter_soro_exam_amostra (nr_seq_amostra_rack_p bigint) RETURNS varchar AS $body$
DECLARE


ds_exames_concat_w	varchar(100);


BEGIN
SELECT SUBSTR(obter_select_concatenado_bv(' select distinct el.cd_exame
                                              from lab_soro_exame_amostra lsea
                                             inner join prescr_procedimento pp on pp.nr_prescricao = lsea.nr_prescricao and
                                                                                  pp.nr_sequencia = lsea.nr_seq_prescr
                                             inner join exame_laboratorio el on el.nr_seq_exame = pp.nr_seq_exame
                                             where lsea.nr_seq_amostra_rack = :nr_seq_amostra_rack_p', 'nr_seq_amostra_rack_p='||nr_seq_amostra_rack_p,', '),1,255) ds_exame
into STRICT 	ds_exames_concat_w
;

return	ds_exames_concat_w;

end;
