CREATE OR REPLACE FUNCTION lab_obter_tempo_para_atraso ( nr_prescricao_p bigint, nr_seq_prescr_p bigint default 0) RETURNS bigint AS $body$
DECLARE


ie_retorno_w	bigint;


BEGIN
	select min((OBTER_DIF_DATA(clock_timestamp(),dt_result_atrasado,'TM'))::numeric )qt_min_atrasar
    into STRICT ie_retorno_w
	from prescr_procedimento a,
		 prescr_procedimento_compl b	
	where a.nr_prescricao = nr_prescricao_p
	and (((coalesce(nr_seq_prescr_p::text, '') = '')
    	or (nr_seq_prescr_p = 0)) 
        or (a.nr_sequencia = nr_seq_prescr_p))
	and b.nr_sequencia = a.nr_seq_proc_compl
	and b.dt_result_atrasado > clock_timestamp();

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
