CREATE OR REPLACE FUNCTION lab_obter_tempo_prescr_pardini ( nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_exame_p bigint, nr_seq_amostra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_Seq_exame_equip_w	bigint;
ds_retorno_w		varchar(255);
nr_amostra_w		integer;

BEGIN

select	max(nr_amostra)
into STRICT	nr_amostra_w
from	prescr_proc_material
where	nr_sequencia  = nr_seq_amostra_p;


select 	nr_Seq_exame
into STRICT	nr_Seq_exame_equip_w
from	exame_laboratorio
where	nr_Seq_superior = nr_seq_exame_p
and	nr_ordem_amostra = nr_amostra_w;

if (nr_Seq_exame_equip_w > 0) then

	select	Obter_Equipamento_Exame_Mat(nr_Seq_exame_equip_w,1,nr_seq_material_p,'PARDINI')
	into STRICT	ds_retorno_w
	;



end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
