SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_obter_tot_pedido_lsf (nr_lote_externo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w 	bigint;

BEGIN
qt_retorno_w := 0;

if (nr_lote_externo_p IS NOT NULL AND nr_lote_externo_p::text <> '') then
	if (ie_opcao_p = 'P') then
		select 	count(distinct nr_prescricao)
		into STRICT 	qt_retorno_w
		from 	prescr_procedimento
		where 	nr_seq_lote_externo = nr_lote_externo_p;
	elsif (ie_opcao_p = 'E') then
		select 	count(nr_seq_exame)
		into STRICT 	qt_retorno_w
		from 	prescr_procedimento
		where 	nr_seq_lote_externo = nr_lote_externo_p;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
