CREATE OR REPLACE FUNCTION lab_obter_valores_referencia ( nr_seq_resultado_p exame_lab_result_item.nr_seq_resultado%type, nr_seq_prescr_p exame_lab_result_item.nr_seq_prescr%type, nr_seq_exame_p exame_lab_result_item.nr_seq_exame%type) RETURNS varchar AS $body$
DECLARE

ds_retorno_w 		varchar(2000);
ds_preposicao_w 	varchar(2);
ds_resultado_w		exame_lab_result_item.ds_resultado%type;
ds_resultado_curto_w    exame_lab_result_item.ds_resultado_curto%type;
qt_resultado_w		exame_lab_result_item.qt_resultado%type;
pr_resultado_w        	exame_lab_result_item.pr_resultado%type;
qt_maxima_w        	exame_lab_result_item.qt_maxima%type;
qt_minima_w        	exame_lab_result_item.qt_minima%type;
pr_maximo_w        	exame_lab_result_item.pr_maximo%type;
pr_minimo_w        	exame_lab_result_item.pr_minimo%type;
ds_referencia_w       	exame_lab_result_item.ds_referencia%type;
qt_decimais_w		exame_lab_result_item.qt_decimais%type;

BEGIN


select	ds_resultado,
        ds_resultado_curto,
        qt_resultado,
        pr_resultado,
        coalesce(qt_maxima, -1),
        coalesce(qt_minima, -1),
        coalesce(pr_maximo, -1),
        coalesce(pr_minimo, -1),
       	ds_referencia,
	qt_decimais
into STRICT	ds_resultado_w,
        ds_resultado_curto_w,
        qt_resultado_w,
        pr_resultado_w,
        qt_maxima_w,
        qt_minima_w,
        pr_maximo_w,
        pr_minimo_w,
       	ds_referencia_w,
	qt_decimais_w
from	exame_lab_result_item
where	nr_seq_resultado = nr_seq_resultado_p
and	nr_seq_prescr = nr_seq_prescr_p
and	nr_seq_exame = nr_seq_exame_p;

if (ds_referencia_w IS NOT NULL AND ds_referencia_w::text <> '') then
	ds_retorno_w := ds_referencia_w;
else
	if (coalesce(qt_resultado_w::text, '') = '') then
		if (coalesce(ds_resultado_w::text, '') = '') then
			qt_resultado_w := -1000.0;
		else
			ds_resultado_w := replace(ds_resultado_w,'>=','');
			ds_resultado_w := replace(ds_resultado_w,'<=','');
			ds_resultado_w := replace(ds_resultado_w,'>','');
			ds_resultado_w := replace(ds_resultado_w,'<','');
			ds_resultado_w := replace(ds_resultado_w,' ','');
		end if;
	end if;

	if ((coalesce(qt_resultado_w::text, '') = '') or (length(trim(both translate( ds_resultado_w, '.+-0123456789', ' '))) is null)) then

		ds_preposicao_w := obter_texto_tasy(324549,wheb_usuario_pck.get_nr_seq_idioma);

		if (pr_minimo_w <> -1 or pr_maximo_w <> -1 ) then
			ds_retorno_w := formata_casas_decimais(pr_minimo_w,qt_decimais_w);
			ds_retorno_w := ds_retorno_w || ' ';
			ds_retorno_w := ds_retorno_w || ds_preposicao_w;
			ds_retorno_w := ds_retorno_w || ' ';
			ds_retorno_w := ds_retorno_w || formata_casas_decimais(pr_maximo_w,qt_decimais_w);
		end if;

		if (qt_minima_w <> -1 or qt_maxima_w <> -1 ) then
			if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
				ds_retorno_w := ds_retorno_w || ' ';
			end if;

			ds_retorno_w := ds_retorno_w || formata_casas_decimais(qt_minima_w,qt_decimais_w);
			ds_retorno_w := ds_retorno_w || ' ';
			ds_retorno_w := ds_retorno_w || ds_preposicao_w;
			ds_retorno_w := ds_retorno_w || ' ';
			ds_retorno_w := ds_retorno_w || formata_casas_decimais(qt_maxima_w,qt_decimais_w);
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
