CREATE OR REPLACE FUNCTION lab_obter_valor_padrao ( ds_regra_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w	varchar(255) := '';


BEGIN

ds_resultado_w := null;

if (ds_regra_p IS NOT NULL AND ds_regra_p::text <> '') then
	if (position('|' in ds_regra_p) > 0) then
		ds_resultado_w := substr(ds_regra_p, position('|' in ds_regra_p) + 1, 255);
		ds_resultado_w := substr(ds_resultado_w, 0, position('|' in ds_resultado_w) - 1);
	end if;
end if;

return ds_resultado_w;

end;
