CREATE OR REPLACE FUNCTION lab_obter_vol_amostra (nr_prescricao_p bigint, nr_seq_material_p bigint, nr_seq_grupo_p bigint) RETURNS bigint AS $body$
DECLARE


qt_volume_w	integer;


BEGIN


select	coalesce(max(a.qt_volume),0)
into STRICT	qt_volume_w
from 	prescr_proc_material a
where 	a.nr_prescricao 	= nr_prescricao_p
and	a.nr_seq_material	= nr_seq_material_p
and	((CASE WHEN a.nr_seq_grupo=0 THEN nr_seq_grupo_p  ELSE a.nr_seq_grupo END   = nr_seq_grupo_p) or (coalesce(a.nr_seq_grupo::text, '') = ''));

return qt_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
