SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lab_seq_obter_frasco_exame ( nr_seq_exame_p bigint, cd_material_exame_p text, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_frasco_w		bigint;
ie_prioridade_w		integer;
nr_seq_material_w	bigint;


BEGIN


nr_seq_material_w	:= nr_seq_material_p;

if (cd_material_exame_p IS NOT NULL AND cd_material_exame_p::text <> '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_material_w
	from	material_exame_lab
	where	cd_material_exame = cd_material_exame_p;
end if;

SELECT	coalesce(min(a.ie_prioridade),0)
into STRICT	ie_prioridade_w
FROM	exame_lab_frasco a,
		frasco_exame_lab b
WHERE	a.nr_seq_frasco	  = b.nr_sequencia
AND		a.nr_seq_material 	  = nr_seq_material_w
AND		a.nr_seq_exame 	  = nr_seq_exame_p
and		coalesce(a.ie_situacao,'A') = 'A';


SELECT	min(b.nr_sequencia)
into STRICT	nr_seq_frasco_w
FROM	exame_lab_frasco a,
		frasco_exame_lab b
WHERE	a.nr_seq_frasco		= b.nr_sequencia
AND		a.nr_seq_material	= nr_seq_material_w
AND		a.nr_seq_exame 	 	= nr_seq_exame_p
and		coalesce(ie_prioridade,0)	= ie_prioridade_w
and		coalesce(a.ie_situacao,'A') = 'A';

return	nr_seq_frasco_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
