CREATE OR REPLACE FUNCTION lab_verifica_formato_resultado ( nr_seq_resultado_p bigint, nr_sequencia_p bigint, ds_resultado_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE

nr_seq_exame_w		bigint;
ie_formato_resultado_w	varchar(3);
ie_formato_correto_w	varchar(1);

BEGIN

ie_formato_correto_w := 'N';

select	coalesce(max(a.nr_seq_exame),'0')
into STRICT	nr_seq_exame_w
from	exame_lab_result_item a
where	a.nr_seq_resultado = nr_seq_resultado_p
and	a.nr_sequencia = nr_sequencia_p;

select	coalesce(max(a.ie_formato_resultado),'0')
into STRICT	ie_formato_resultado_w
from	exame_laboratorio a
where	a.nr_seq_exame = nr_seq_exame_w;

if (ie_formato_resultado_w  in ('D', 'DL', 'DD', 'DV', 'V', 'P', 'VP', 'CV')) then
	ie_formato_correto_w := 'S';
end if;

return	ie_formato_correto_w;

end;
