SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION latam_obter_dados_gap (nr_seq_gap_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

ds_retorno_w	double precision;

BEGIN

if (ie_opcao_p = 'T') then
	select	count(*)
	into STRICT	ds_retorno_w
	from	LATAM_REQUISITO
	WHERE  	nr_seq_gap    = nr_Seq_gap_p
	and ie_type_requirement = 'BR';
elsif (ie_opcao_p = 'N') then
	SELECT 	count(*)
	into STRICT	ds_retorno_w
	FROM   LATAM_REQUISITO
	WHERE  	nr_seq_gap    = nr_Seq_gap_p
	and		IE_SITUACAO in ('D','P')
	and ie_type_requirement = 'BR';
elsif (ie_opcao_p = 'V') then	
	SELECT 	coalesce(SUM(vl_Custo),0)
	into STRICT	ds_retorno_w
	FROM   	LATAM_REQUISITO
	WHERE  	nr_seq_gap    = nr_Seq_gap_p;
elsif (ie_opcao_p = 'H') then		
	SELECT SUM(QT_TEMPO_HORAS)
	into STRICT	ds_retorno_w
	FROM   LATAM_REQUISITO
	WHERE  	nr_seq_gap    = nr_Seq_gap_p;
elsif (ie_opcao_p = 'TG') then		
	SELECT SUM(QT_TEMPO_HORAS) + SUM(coalesce(QT_HORAS_DESIGN,0)) + SUM(coalesce(QT_HORAS_TEC,0))
	into STRICT	ds_retorno_w
	FROM   LATAM_REQUISITO
	WHERE  	nr_seq_gap    = nr_Seq_gap_p;
		
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
