SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION laudo_obter_cd_integracao ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_tipo_integracao_p bigint) RETURNS varchar AS $body$
DECLARE

 
/* IE_TIPO_INTEGRACAO 
3 - Fleury 
*/
 
				 
nr_idade_w				bigint;
cd_pessoa_fisica_w		pessoa_fisica.cd_pessoa_fisica%type;
ie_sexo_w				pessoa_fisica.ie_sexo%type;
nr_atendimento_w		atendimento_paciente.nr_atendimento%type;
ie_tipo_atendimento_w	atendimento_paciente.ie_tipo_atendimento%type;
cd_estabelecimento_w	prescr_medica.cd_estabelecimento%type;
nr_seq_proc_interno_w	prescr_procedimento.nr_seq_proc_interno%type;
cd_integracao_w			regra_proc_interno_integra.cd_integracao%type;
				

BEGIN 
 
select	max(a.nr_seq_proc_interno) 
into STRICT	nr_seq_proc_interno_w 
from	prescr_procedimento a 
where	a.nr_prescricao = nr_prescricao_p 
and		a.nr_sequencia = nr_seq_prescricao_p 
and		coalesce(a.nr_seq_exame::text, '') = '';
 
select	max(a.cd_pessoa_fisica), 
		max(a.nr_atendimento), 
		max(a.cd_estabelecimento) 
into STRICT	cd_pessoa_fisica_w, 
		nr_atendimento_w, 
		cd_estabelecimento_w 
from	prescr_medica a 
where	a.nr_prescricao = nr_prescricao_p;
 
select	max(a.ie_tipo_atendimento) 
into STRICT	ie_tipo_atendimento_w 
from	atendimento_paciente a 
where	a.nr_atendimento = nr_atendimento_w;
 
select	obter_somente_numero(obter_idade(a.dt_nascimento, clock_timestamp(), 'A')), 
		a.ie_sexo 
into STRICT	nr_idade_w, 
		ie_sexo_w 
from	pessoa_fisica a 
where	a.cd_pessoa_fisica = cd_pessoa_fisica_w;
 
select	max(a.cd_integracao) 
into STRICT	cd_integracao_w 
from	regra_proc_interno_integra a 
where	a.nr_seq_proc_interno = nr_seq_proc_interno_w 
and		a.ie_tipo_integracao = ie_tipo_integracao_p 
and		((coalesce(qt_idade_min::text, '') = '' and coalesce(a.qt_idade_max::text, '') = '') or (nr_idade_w between coalesce(a.qt_idade_min,0) and coalesce(a.qt_idade_max,800))) 
and		((coalesce(a.ie_sexo::text, '') = '') or (a.ie_sexo = ie_sexo_w)) 
and		((coalesce(a.ie_tipo_atendimento::text, '') = '') or (ie_tipo_atendimento_w = a.ie_tipo_atendimento)) 
and		((coalesce(a.cd_estabelecimento::text, '') = '') or (a.cd_estabelecimento = cd_estabelecimento_w));
 
return	cd_integracao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION laudo_obter_cd_integracao ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_tipo_integracao_p bigint) FROM PUBLIC;

