SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION laudo_obter_dados_exame_compl (nr_seq_exame_compl_p bigint, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_tipo_dado_p
	N - nome do usuário
	D - data de liberação
*/
ds_retorno_w 	varchar(255);


BEGIN
if (nr_seq_exame_compl_p IS NOT NULL AND nr_seq_exame_compl_p::text <> '') then

	if (ie_tipo_dado_p = 'N') then
		begin
			select 	MAX(r.nm_usuario)
			into STRICT 	ds_retorno_w
			from   	prescr_proc_exame_compl r,
				patologia_exame_compl p
			where  	r.nr_seq_pato_exame = p.nr_sequencia
			and	r.nr_sequencia = nr_seq_exame_compl_p;
		end;
	elsif (ie_tipo_dado_p = 'D' )	then
		begin
			select 	to_char(MAX(r.dt_atualizacao),'dd/mm/yyyy hh24:mi:ss')
			into STRICT 	ds_retorno_w
			from   	prescr_proc_exame_compl r,
				patologia_exame_compl p
			where  	r.nr_seq_pato_exame = p.nr_sequencia
			and	r.nr_sequencia = nr_seq_exame_compl_p;
		end;
	end if;
end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
