CREATE OR REPLACE FUNCTION laudo_obter_se_hor_suspenso ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'N';


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_prescricao_p IS NOT NULL AND nr_seq_prescricao_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from  	prescr_proc_hor a
	where	(a.dt_suspensao IS NOT NULL AND a.dt_suspensao::text <> '')
	and	a.nr_prescricao = nr_prescricao_p
	and	a.nr_seq_procedimento = nr_seq_prescricao_p;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
