CREATE OR REPLACE FUNCTION lic_obter_cgc_reg_preco_solic ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_licitacao_w		bigint;
nr_seq_lic_item_w		bigint;
nr_seq_fornec_w		bigint;
cd_cgc_fornec_w		varchar(14);


BEGIN
select	coalesce(max(nr_seq_licitacao), 0),
	coalesce(max(nr_seq_lic_item), 0)
into STRICT	nr_seq_licitacao_w,
	nr_seq_lic_item_w
from	reg_lic_item_solic
where	nr_solic_compra	  	= nr_solic_compra_p
and	nr_item_solic_compra	= nr_item_solic_compra_p;

if (nr_seq_licitacao_w > 0) and (nr_seq_lic_item_w > 0) then

	select	lic_obter_forn_vencedor_item(nr_seq_licitacao_w, nr_seq_lic_item_w)
	into STRICT	nr_seq_fornec_w
	;

	select	cd_cgc_fornec
	into STRICT	cd_cgc_fornec_w
	from	reg_lic_fornec
	where	nr_sequencia	= nr_seq_fornec_w;
end if;

return	cd_cgc_fornec_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
