CREATE OR REPLACE FUNCTION lic_obter_cor_legenda ( nr_seq_estagio_p bigint, ie_cor_p text) RETURNS varchar AS $body$
DECLARE



ds_cor_w	varchar(15);
ds_cor_fundo_w	varchar(15);
ds_cor_fonte_w	varchar(15);

BEGIN

select	ds_cor_fundo,
	ds_cor_fonte
into STRICT	ds_cor_fundo_w,
	ds_cor_fonte_w
from	lic_estagio
where	nr_sequencia = nr_seq_estagio_p;

if (ie_cor_p	= 'FU') then
	ds_cor_w := ds_cor_fundo_w;
elsif (ie_cor_p	= 'FO') then
	ds_cor_w := ds_cor_fonte_w;
end if;

return	ds_cor_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
