CREATE OR REPLACE FUNCTION lic_obter_dados_lote ( nr_seq_licitacao_p bigint, nr_lote_compra_p text, ie_opcao_p bigint) RETURNS bigint AS $body$
DECLARE


vl_maximo_edital_w			double precision;
qt_item_w			double precision;
vl_retorno_w			double precision;

/*ie_opcao_p
1 - VL_MAXIMO_EDITAL
2 - QT_ITEM */
BEGIN

select	sum(vl_maximo_edital * qt_item),
	sum(qt_item)
into STRICT	vl_maximo_edital_w,
	qt_item_w
from	reg_lic_item
where	nr_seq_licitacao 	= nr_seq_licitacao_p
and	nr_lote_compra	= nr_lote_compra_p
and	coalesce(ie_lote,'N')	= 'N';

if (ie_opcao_p = 1) then
	vl_retorno_w := vl_maximo_edital_w;
elsif (ie_opcao_p = 2) then
	vl_retorno_w := qt_item_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
