CREATE OR REPLACE FUNCTION lic_obter_dados_modadalidade ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao
M - CD_MODALIDADE
R - IE_REG_PRECO_EXPORTACAO*/
cd_modalidade_w			varchar(15);
ie_reg_preco_exportacao_w	varchar(1);


BEGIN

select	cd_modalidade,
	ie_reg_preco_exportacao
into STRICT	cd_modalidade_w,
	ie_reg_preco_exportacao_w
from	reg_lic_mod_compra
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'M') then
	return	cd_modalidade_w;
elsif (ie_opcao_p = 'R') then
	return	ie_reg_preco_exportacao_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
