CREATE OR REPLACE FUNCTION lic_obter_datas ( nr_sequencia_p bigint, ie_opcao_p bigint) RETURNS timestamp AS $body$
DECLARE

/*
0 - DT_EMISSAO
1 - DT_LIBERACAO
2 - DT_CONFIRMACAO_EDITAL
3 - DT_APROVACAO_EDITAL
4 - DT_PUBLICACAO_EDITAL
*/
dt_emissao_w			timestamp;
dt_liberacao_w			timestamp;
dt_confirmacao_edital_w		timestamp;
dt_aprovacao_edital_w		timestamp;
dt_publicacao_edital_w		timestamp;


BEGIN
select	dt_emissao,
	dt_liberacao,
	dt_confirmacao_edital,
	dt_aprovacao_edital,
	dt_publicacao_edital
into STRICT	dt_emissao_w,
	dt_liberacao_w,
	dt_confirmacao_edital_w,
	dt_aprovacao_edital_w,
	dt_publicacao_edital_w
from	reg_licitacao
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 0) then
	return dt_emissao_w;
elsif (ie_opcao_p = 1) then
	return dt_liberacao_w;
elsif (ie_opcao_p = 2) then
	return dt_confirmacao_edital_w;
elsif (ie_opcao_p = 3) then
	return dt_aprovacao_edital_w;
elsif (ie_opcao_p = 4) then
	return dt_publicacao_edital_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION lic_obter_datas ( nr_sequencia_p bigint, ie_opcao_p bigint) FROM PUBLIC;

