CREATE OR REPLACE FUNCTION lic_obter_desc_fornec (nr_seq_fornec_p bigint) RETURNS varchar AS $body$
DECLARE


ds_nome_abrev_w		pessoa_juridica.ds_nome_abrev%type;
nm_fantasia_w		pessoa_juridica.nm_fantasia%type;
ds_razao_social_w	pessoa_juridica.ds_razao_social%type;
ds_retorno_w		varchar(254);



BEGIN
Select	ds_nome_abrev,
	nm_fantasia,
	ds_razao_social
into STRICT	ds_nome_abrev_w,
	nm_fantasia_w,
	ds_razao_social_w
from	pessoa_juridica
where	cd_cgc = (SELECT	cd_cgc_fornec
		  from		lic_fornec
		  where		nr_sequencia = nr_seq_fornec_p);
if (ds_nome_abrev_w IS NOT NULL AND ds_nome_abrev_w::text <> '') then
	ds_retorno_w := ds_nome_abrev_w;
elsif (nm_fantasia_w IS NOT NULL AND nm_fantasia_w::text <> '') then
	ds_retorno_w := nm_fantasia_w;
else
	ds_retorno_w := ds_razao_social_w;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
