CREATE OR REPLACE FUNCTION lic_obter_desc_mat_lote ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w		integer;
ds_mat_lote_w		varchar(255);


BEGIN

select	coalesce(max(cd_material),0),
	substr(WHEB_MENSAGEM_PCK.get_texto(310788) || max(nr_lote_compra),1,255)
into STRICT	cd_material_w,
	ds_mat_lote_w
from	reg_lic_item
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p;

if (cd_material_w > 0) then
	ds_mat_lote_w := substr(obter_desc_material(cd_material_w),1,255);
end if;

return	ds_mat_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
