CREATE OR REPLACE FUNCTION lic_obter_desc_parecer_item (nr_seq_parecer_p bigint) RETURNS varchar AS $body$
DECLARE


ds_parecer_w	varchar(600);


BEGIN
select	wheb_mensagem_pck.get_Texto(802165) || ': ' || a.nm_comercial || chr(13) || wheb_mensagem_pck.get_Texto(802167) || ': ' || b.ds_fabricante || chr(13)
	|| wheb_mensagem_pck.get_Texto(802168) || ': ' || a.ie_parecer || chr(13) || wheb_mensagem_pck.get_Texto(802169)|| ': ' || a.dt_parecer
into STRICT	ds_parecer_w
from	mat_fabricante b,
	lic_parecer_item a
where	b.nr_sequencia = a.nr_seq_fabric
and	a.nr_sequencia = nr_seq_parecer_p;
return	ds_parecer_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
