CREATE OR REPLACE FUNCTION lic_obter_empates_fornec ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, nr_seq_fornec_p bigint) RETURNS bigint AS $body$
DECLARE


vl_inicial_w		double precision;
qt_registros_w		bigint;


BEGIN

select	max(vl_inicial)
into STRICT	vl_inicial_w
from	reg_lic_fornec_lance
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p
and	nr_seq_fornec = nr_seq_fornec_p
and	nr_seq_lance = 1;

select	count(*)
into STRICT	qt_registros_w
from	reg_lic_fornec_lance
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p
and	vl_inicial = vl_inicial_w
and	nr_seq_lance = 1;

return	qt_registros_w;

end;
$body$
