CREATE OR REPLACE FUNCTION lic_obter_marca_material ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint, cd_cgc_fornec_p text) RETURNS varchar AS $body$
DECLARE



ds_marca_w				varchar(255);
nr_seq_fornec_w				bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_fornec_w
from	reg_lic_fornec
where	nr_seq_licitacao	= nr_seq_licitacao_p
and	cd_cgc_fornec	= cd_cgc_fornec_p;

if (nr_seq_fornec_w > 0) then

	select	ds_marca
	into STRICT	ds_marca_w
	from	reg_lic_item_fornec
	where	nr_seq_fornec	= nr_seq_fornec_w
	and	nr_seq_lic_item	= nr_seq_lic_item_p;

end if;

return	ds_marca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
