SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_melhor_vl_fornec ( nr_seq_fornec_p bigint, nr_seq_lic_item_p bigint) RETURNS bigint AS $body$
DECLARE


vl_item_w			double precision;
nr_seq_licitacao_w		bigint;


BEGIN

select	coalesce(min(vl_item), 0)
into STRICT	vl_item_w
from	reg_lic_fornec_lance
where	nr_seq_fornec		= nr_seq_fornec_p
and	nr_seq_lic_item		= nr_seq_lic_item_p
and	vl_item > 0
and	coalesce(ie_qualificado, 'S') 	= 'S';

if (vl_item_w = 0) then

	select	nr_seq_licitacao
	into STRICT	nr_seq_licitacao_w
	from	reg_lic_fornec
	where	nr_sequencia = nr_seq_fornec_p;

	select	lic_obter_proposta_inicial(nr_seq_licitacao_w, nr_seq_fornec_p, nr_seq_lic_item_p)
	into STRICT	vl_item_w
	;
end if;

return	vl_item_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
