CREATE OR REPLACE FUNCTION lic_obter_ordem_preco (nr_seq_item_p bigint, nr_seq_fornec_p bigint) RETURNS varchar AS $body$
DECLARE


nr_ordem_w			bigint;
nr_ordem_ww			bigint;
nr_ordem_retorno_w		bigint;
nr_seq_fornec_w		bigint;
vl_item_w			double precision;
vl_anterior_w			double precision;
ie_vencedor_w			varchar(1);

c01 CURSOR FOR
	SELECT	nr_seq_fornec,
		vl_item,
		ie_vencedor
	from	lic_item_fornec
	where	nr_seq_item = nr_seq_item_p
	and	vl_item > 0
	order	by 	ie_vencedor desc,
			vl_item asc;


BEGIN

nr_ordem_w	:= 0;
nr_ordem_ww	:= 0;
open c01;
loop
fetch c01 into
	nr_seq_fornec_w,
	vl_item_w,
	ie_vencedor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	nr_ordem_ww		:= nr_ordem_ww + 1;
	if (nr_ordem_w = 0) then
		nr_ordem_w	:= nr_ordem_w + 1;
	elsif (vl_item_w	= vl_anterior_w) and (nr_ordem_w	= 1)	then
		nr_ordem_w	:= nr_ordem_w + 1;
	elsif (vl_item_w	<> vl_anterior_w) then
		nr_ordem_w	:= nr_ordem_ww;
	end if;
	vl_anterior_w		:= vl_item_w;
	if (nr_seq_fornec_w = nr_seq_fornec_p) then
		nr_ordem_retorno_w := nr_ordem_w;
		exit;
	end if;

end loop;
close c01;
return to_char(nr_ordem_retorno_w);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
