CREATE OR REPLACE FUNCTION lic_obter_parecer_item (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_fabricante_w		varchar(80);
nm_comercial_w		varchar(80);
dt_parecer_w			timestamp;
ds_parecer_w			varchar(255);


BEGIN
ds_parecer_w			:= '';
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	Select	ds_fabricante,
		nm_comercial,
		dt_parecer
	into STRICT	ds_fabricante_w,
		nm_comercial_w,
		dt_parecer_w
	from	mat_fabricante b,
		lic_parecer_item a
	where	a.nr_sequencia	= nr_sequencia_p
	and	a.nr_seq_fabric	= b.nr_sequencia;
	ds_parecer_w	:= nm_comercial_w || ' - ' || ds_fabricante_w ||
			' - ' || to_char(dt_parecer_w,'dd/mm/yyyy');
end if;

return ds_parecer_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
