DECLARE


nr_sequencia_w			bigint;


BEGIN


select	max(a.nr_sequencia)
into STRICT	nr_sequencia_w
from	reg_compra a,
	reg_compra_item b
where	a.nr_sequencia	= b.nr_seq_reg_compra
and	a.nr_seq_licitacao	= nr_seq_licitacao_p
