CREATE OR REPLACE FUNCTION lic_obter_reg_edital_contr_oc ( nr_ordem_compra_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_licitacao_w			bigint;
nr_contrato_w			bigint;
nr_controle_edital_w		varchar(100);

BEGIN

select	coalesce(max(nr_contrato),0)
into STRICT	nr_contrato_w
from	ordem_compra_item
where	nr_ordem_compra = nr_ordem_compra_p;

if (nr_contrato_w > 0) then
	select	max(nr_seq_licitacao)
	into STRICT	nr_seq_licitacao_w
	from	reg_lic_contrato
	where	nr_seq_contrato = nr_contrato_w;

	nr_controle_edital_w := lic_obter_nr_controle_edital(nr_seq_licitacao_w);
end if;


return	nr_controle_edital_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
