CREATE OR REPLACE FUNCTION lic_obter_saldo_item_fornec (nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_lic_item_w	bigint;
qt_item_w		double precision;
qt_ordem_w		double precision;
qt_saldo_w		double precision;


BEGIN

select	a.qt_item,
	a.nr_sequencia
into STRICT	qt_item_w,
	nr_seq_lic_item_w
from	lic_item a,
	lic_item_fornec b
where	a.nr_sequencia	= b.nr_seq_item
and	b.nr_sequencia	= nr_sequencia_p;

select	coalesce(sum(qt_material), 0)
into STRICT	qt_ordem_w
from	ordem_compra_item
where	nr_seq_lic_item = nr_seq_lic_item_w;

qt_saldo_w := qt_item_w - qt_ordem_w;

return	qt_saldo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
