SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_saldo_item_solic ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_usado_w			double precision := 0;
qt_item_w			double precision := 0;
qt_item_solic_w			double precision := 0;
qt_result_w			double precision := 0;
qt_comprado_w			double precision := 0;
qt_cancelado_w			double precision := 0;
nr_solic_compra_w			bigint;
nr_item_solic_compra_w		integer;

c01 CURSOR FOR 
SELECT	coalesce(sum(a.qt_item),0), 
	a.nr_solic_compra,			 
	a.nr_item_solic_compra		 
from	reg_lic_item_solic a, 
	reg_lic_compra_item b 
where	a.nr_seq_lic_item		= b.nr_seq_lic_item 
and	a.nr_seq_licitacao		= b.nr_seq_licitacao 
and	a.nr_solic_compra		= b.nr_solic_compra 
and	a.nr_item_solic_compra	= b.nr_item_solic_compra 
and	a.nr_seq_licitacao		= nr_seq_licitacao_p 
and	a.nr_seq_lic_item		= nr_seq_lic_item_p 
and exists ( 
	SELECT	1 
	from	ordem_compra x, 
		ordem_compra_item y 
	where	x.nr_ordem_compra			= y.nr_ordem_compra 
	and	x.nr_seq_reg_lic_compra		= b.nr_seq_reg_lic_compra 
	and	y.nr_seq_reg_lic_item		= a.nr_seq_lic_item 
	and	y.cd_material			= b.cd_material 
	and	(x.dt_aprovacao IS NOT NULL AND x.dt_aprovacao::text <> '') 
	and	coalesce(x.nr_seq_motivo_cancel::text, '') = '' 
	and	(y.dt_aprovacao IS NOT NULL AND y.dt_aprovacao::text <> '')) 
group by a.nr_solic_compra, 
	a.nr_item_solic_compra;


BEGIN 
 
select	coalesce(sum(qt_item),0) --0 
into STRICT	qt_item_w 
from	reg_compra_item 
where	nr_seq_licitacao	= nr_seq_licitacao_p 
and	nr_seq_lic_item	= nr_seq_lic_item_p;
--and	dt_cancelamento is null; 	OS 805126 Retirado porque estando cancelado ou n√£o, deve mostrar o valor oficial. Conversado com o Maicon 
 
open C01;
loop 
fetch C01 into	 
	qt_item_solic_w, 
	nr_solic_compra_w, 
	nr_item_solic_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	select	coalesce(sum(c.qt_prevista_entrega),0) 
	into STRICT	qt_cancelado_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra		= b.nr_ordem_compra 
	and	c.nr_ordem_compra		= b.nr_ordem_compra 
	and	c.nr_item_oci		= b.nr_item_oci 
	and	b.nr_solic_compra		= nr_solic_compra_w 
	and	b.nr_item_solic_compra	= nr_item_solic_compra_w 
	and	((a.nr_seq_motivo_cancel IS NOT NULL AND a.nr_seq_motivo_cancel::text <> '') or (c.dt_cancelamento IS NOT NULL AND c.dt_cancelamento::text <> ''));
	 
	select	coalesce(sum(c.qt_prevista_entrega),0) 
	into STRICT	qt_comprado_w 
	from	ordem_compra a, 
		ordem_compra_item b, 
		ordem_compra_item_entrega c 
	where	a.nr_ordem_compra		= b.nr_ordem_compra 
	and	c.nr_ordem_compra		= b.nr_ordem_compra 
	and	c.nr_item_oci		= b.nr_item_oci 
	and	b.nr_solic_compra		= nr_solic_compra_w 
	and	b.nr_item_solic_compra	= nr_item_solic_compra_w;
		 
	 
	qt_usado_w := qt_usado_w + qt_comprado_w - qt_cancelado_w;
 
	 
	end;
end loop;
close C01;
 
 
qt_result_w := qt_item_w - qt_usado_w;
 
return	qt_result_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
