CREATE OR REPLACE FUNCTION lic_obter_saldo_item ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_saldo_w		double precision;
qt_item_oc_w		double precision;
qt_tot_item_oc_w	double precision;
qt_item_w		double precision;
qt_tot_item_w		double precision;
nr_seq_item_w		integer;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia, 
		a.qt_item 
	from	lic_item a, 
		lic_licitacao b, 
		lic_estagio c 
	where	a.nr_seq_licitacao = b.nr_sequencia 
	and	b.nr_seq_estagio = c.nr_sequencia 
	and	c.ie_permite_oc = 'S' 
	and	cd_material = cd_material_p;


BEGIN 
qt_tot_item_oc_w := 0;
qt_tot_item_w := 0;
OPEN C01;
LOOP 
FETCH C01 INTO 
	nr_seq_item_w, 
	qt_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	 
	qt_tot_item_w := qt_tot_item_w + qt_item_w;
 
	select	coalesce(sum(d.qt_material), 0) 
	into STRICT	qt_item_oc_w 
	from	ordem_compra_item d, 
		ordem_compra f 
	where	f.nr_ordem_compra = d.nr_ordem_compra 
	and	f.cd_estabelecimento = cd_estabelecimento_p 
	and	d.nr_seq_lic_item = nr_seq_item_w;
 
	qt_tot_item_oc_w := qt_tot_item_oc_w + qt_item_oc_w;
	 
 
END LOOP;
CLOSE C01;
 
qt_saldo_w := qt_tot_item_w - qt_tot_item_oc_w;
 
return	qt_saldo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
