SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_se_aceito_recurso ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_aceito_w			varchar(1) := 'S';
qt_existe_w			bigint;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_recurso a,
	reg_lic_item_recurso b
where	a.nr_sequencia		= b.nr_seq_recurso
and	a.nr_seq_licitacao	= nr_seq_licitacao_p
and	b.nr_seq_lic_item	= nr_seq_lic_item_p
and	coalesce(b.ie_aceito,'P')	= 'P';

/*Se tem pendente, não é aceito. Caso contrario é aceito*/

if (qt_existe_w > 0) then
	ie_aceito_w := 'N';
end if;

return	ie_aceito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
