CREATE OR REPLACE FUNCTION lic_obter_se_deserto_frustrado ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w			bigint;
vl_vencedor_w			double precision;
ie_status_w			varchar(1);
qt_desqualificado_w		bigint;

/*
ie_deserto_frustrado_w
	N - Desqualificado
	D - Deserto
	F - Frustrado
*/
BEGIN

select	count(*)
into STRICT	qt_desqualificado_w
from	reg_lic_item_fornec
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p
and	ie_qualificado = 'N';

select	count(*)
into STRICT	qt_registro_w
from	reg_lic_item_fornec
where	nr_seq_licitacao = nr_seq_licitacao_p
and	nr_seq_lic_item = nr_seq_lic_item_p
and	coalesce(vl_original,0) > 0;

if (qt_desqualificado_w > 0) then
	ie_status_w := 'N';
elsif (qt_registro_w = 0) then
	ie_status_w := 'D';
else
	select	coalesce(lic_obter_valor_vencedor_item(nr_seq_licitacao_p, nr_seq_lic_item_p),0)
	into STRICT	vl_vencedor_w
	;

	if (vl_vencedor_w = 0) then
		ie_status_w := 'F';
	end if;
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
