SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_se_existe_pend_homol (nr_seq_licitacao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w		bigint;
ie_existe_w		varchar(10) := 'N';


BEGIN

select  count(*)
into STRICT	qt_existe_w
from    reg_lic_homol_itens a
where   a.nr_seq_licitacao = nr_seq_licitacao_p
and	coalesce(a.ie_homologado,'P') = 'P'
and not exists (
	SELECT  1
	from    reg_lic_homol_itens b
	where   a.nr_seq_licitacao  	= b.nr_seq_licitacao
	and	a.nr_seq_lic_item	= b.nr_seq_lic_item
	and     coalesce(b.ie_homologado,'P') <> 'P');

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
