CREATE OR REPLACE FUNCTION lic_obter_se_item_existe_sc ( nr_seq_reg_compra_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


/*Function para identificar se o item do registro de preço está vinculado com alguam solicitação de compras*/

ie_existe_w		varchar(1) := 'N';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_item_solic a,
	solic_compra_item b
where	a.nr_solic_compra = b.nr_solic_compra
and	a.nr_item_solic_compra = b.nr_item_solic_compra
and	lic_obter_registro_preco(a.nr_seq_licitacao, a.nr_seq_lic_item) = nr_seq_reg_compra_p
and	b.cd_material = cd_material_p;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
