CREATE OR REPLACE FUNCTION lic_obter_se_item_homologado ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_homologado_w			varchar(1) := 'N';
qt_existe_w			bigint;

BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_lic_homol_itens a
where	a.nr_seq_lic_item		= nr_seq_lic_item_p
and	nr_seq_licitacao		= nr_seq_licitacao_p
and	coalesce(a.ie_homologado,'N')	= 'S';

if (qt_existe_w > 0) then
	ie_homologado_w := 'S';
end if;

return	ie_homologado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
