CREATE OR REPLACE FUNCTION lic_obter_se_item_lote ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_lote_w		varchar(1) := 'N';
nr_lote_compra_w	varchar(80);


BEGIN

select	coalesce(nr_lote_compra,'')
into STRICT	nr_lote_compra_w
from	reg_lic_item
where	nr_seq_licitacao	= nr_seq_licitacao_p
and	nr_seq_lic_item	= nr_seq_lic_item_p;

if (nr_lote_compra_w IS NOT NULL AND nr_lote_compra_w::text <> '') then
	ie_lote_w := 'S';
end if;

return	ie_lote_w;

end;
$body$
