SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION lic_obter_se_item_reavaliacao ( nr_seq_licitacao_p bigint, nr_seq_lic_item_p bigint) RETURNS varchar AS $body$
DECLARE


ie_reavaliacao_w		varchar(1) := 'N';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	reg_compra_item a
where	a.nr_seq_licitacao = nr_seq_licitacao_p
and	a.nr_seq_lic_item = nr_seq_lic_item_p
and exists (
	SELECT	1
	from	reg_lic_reequilibrio b
	where	a.nr_Sequencia = b.nr_seq_reg_compra_item
	and	coalesce(b.dt_aprovacao::text, '') = '');

if (qt_existe_w > 0) then
	ie_reavaliacao_w := 'S';
end if;

return	ie_reavaliacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
