CREATE OR REPLACE FUNCTION lic_obter_se_reg_preco_cancel ( nr_seq_reg_compra_p bigint, nr_seq_lic_item_p bigint, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


/*Function para saber se o item do registro de preço está cancelado. Utilziado na solicitação de compras, pasta Licitação.*/

ie_cancelado_w		varchar(1) := 'N';
dt_cancelamento_w	timestamp;


BEGIN

select	max(dt_cancelamento)
into STRICT	dt_cancelamento_w
from	reg_compra_item
where	nr_seq_reg_compra	= nr_seq_reg_compra_p
and	nr_seq_lic_item 	= nr_seq_lic_item_p
and	cd_material		= cd_material_p;

if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
	ie_cancelado_w := 'S';
end if;

return	ie_cancelado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
